var ts = require("./typescript")

module.exports.TransformES5 = function(src) {
    var ret = ts.transpileModule(src, {
        compilerOptions: {
            target: ts.ScriptTarget.ES5,
            module: ts.ModuleKind.CommonJS,
            allowJs: true,
            sourceMap: false,
            declaration: false,
        }
    })
    return ret.outputText
}